export const theme = {
  colors: {
    white: '#ffffff',
    black: '#000000',
    whiteAlpha: '#ffffff60',
    accent: '#F59256',
    hover: '#ff6101',
    primaryBackground: '#E5E5E5',
    imageBackground: '#F6F6F6',
    // burgerBackground: '#FDF7F2',
    border: '#E8E8E8',

    text: {
      sectionTitle: '#212121',
      primaryText: '#212121',
      secondaryText: '#BDBDBD',
      emailText: 'rgba(33, 33, 33, 0.8)',
      // accent: '#F59256',
      link: '#1B4371',
    },

    button: {
      primaryBackground: '#FF6C00',
      secondaryBackground: '#F6F6F6',
      accent: '#FF6C00',
      border: '#FF6C00',
      primaryText: '#FFFFFF',
      secondaryText: '#FFFFFF',
      none: 'transparent',
    },

    form: {
      formTitle: '#111111',
      formText: '#535353',
      formPlaceholder: '#535353',
      formBackground: '#F6F6F6',
      formInputBorder: '#E8E8E8',
      formInputAccentBorder: '#FF6C00',

      // formInputBorder: '#E0E0E0',
      // formErrorMessage: '#FC842D',
      // checkboxText: '#9B9FAA',
      // checkboxAccentText: '#FC842D',
      // checkboxAccent: '#FC842D',
      // checkboxBorder: '#E0E0E0',
    },
  },

  space: [0, 2, 4, 8, 16, 32, 64, 128, 256, 512, 1024],

  fonts: {
    logo: 'Poppins, sans-serif',
    body: 'Roboto, sans-serif',
    // heading: 'Gotham Pro, sans-serif',
    main: 'Manrope, sans-serif',
    forminput: 'Inter, sans-serif',
  },

  textTransform: {
    cap: 'capitalize',
    lowCase: 'lowercase',
    upCase: 'uppercase',
  },

  fontSizes: {
    xxs: '12px',
    xs: '14px',
    s: '16px',
    sm: '20px',
    m: '17px',
    l: '30px',
    xl: '32px',
    xxl: '48px',
    xxxl: '68px',
  },

  fontWeights: {
    thin: 100,
    extraLight: 200,
    light: 300,
    normal: 400,
    medium: 500,
    semiBold: 600,
    bold: 700,
    extraBold: 800,
  },

  letterSpacing: {
    xs: '-0.0408px',
    s: '0.01em',
    m: '0.03em',
    l: '0.04em',
    xl: '0.05em',
    xxl: '0.07em',
  },

  lineHeights: {
    text: 1.385,
    dataText: 1.2,
    form: 1.19,
    title: 1.295,
    titleForm: 1.17,
    button: 1.17,
    hero: 1.47,
  },

  borders: {
    none: 'none',
    normal: '1px solid',
  },

  radii: {
    none: '0',
    sm: '4px',
    md: '8px',
    lg: '16px',
    xl: '25px',
    xxl: '30px',
    xxxl: '40px',
    xxxxl: '100px',
    round: '50%',
  },
  animation: {
    cubic: '250ms cubic-bezier(0.4, 0, 0.2, 1)',
  },

  breakpoints: ['320px', '480px', '768px', '1200px'],
};
